// ── 진화 전용 포켓몬 (일일 추천 풀에는 없지만 진화 후 등장) ──
export interface ExtraPokemonData {
  id: number;
  name: string;
  nameEn: string;
  types: PokemonType[];
  evolvesTo?: number;
}

export const EXTRA_POKEMON_DATA: ExtraPokemonData[] = [];

// 이브이 진화 옵션
export const EEVEE_EVOLUTIONS = [134, 135, 136, 196, 197];

// 포켓몬 표시용 데이터 조회 (POKEMON_DATA + EXTRA_POKEMON_DATA 통합)
export function getPokemonDisplayData(id: number): { name: string; nameEn?: string; types: PokemonType[]; evolvesTo?: number } | undefined {
  const main = POKEMON_DATA.find((p) => p.id === id);
  if (main) return { name: main.name, nameEn: main.nameEn, types: main.types, evolvesTo: main.evolvesTo };
  const extra = EXTRA_POKEMON_DATA.find((p) => p.id === id);
  if (extra) return { name: extra.name, nameEn: extra.nameEn, types: extra.types, evolvesTo: extra.evolvesTo };
  return undefined;
}

// 스프라이트 URL 생성
export function getSpriteUrl(id: number): string {
  return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/${id}.gif`;
}

export function getPokemonById(id: number): PokemonData | undefined {
  return POKEMON_DATA.find((p) => p.id === id);
}

export const ALL_POKEMON_IDS = POKEMON_DATA.map((p) => p.id);
export const POKEMON_COUNT = POKEMON_DATA.length;
